    11→/**
    12→ * Log level enumeration
    13→ */
    14→export enum LogLevel {
    15→  DEBUG = 0,
    16→  INFO = 1,
    17→  WARN = 2,
    18→  ERROR = 3,
    19→}
    20→
    21→/**
    22→ * Logger interface for dual-output logging (file + console)
    23→ */
    24→export interface Logger {
    25→  debug(message: string): void;
    26→  info(message: string): void;
    27→  warn(message: string): void;
    28→  error(message: string): void;
    29→  getLogFile(): string;
    30→}
