    32→/**
    33→ * Logger implementation that writes to both console and file
    34→ */
    35→class AdwLogger implements Logger {
    36→  private logFile: string;
    37→
    38→  constructor(adwId: string, logFile: string, triggerType: string) {
    39→    this.logFile = logFile;
    40→    // Store for future use
    41→    void adwId;
    42→    void triggerType;
    43→  }
    44→
    45→  private log(level: LogLevel, message: string): void {
    46→    const timestamp = new Date().toISOString().replace('T', ' ').slice(0, 19);
    47→    const levelName = LogLevel[level];
    48→
    49→    // File format: timestamp - level - message
    50→    const fileMessage = `${timestamp} - ${levelName} - ${message}\n`;
    51→
    52→    // Console format: just the message for INFO and above
    53→    if (level >= LogLevel.INFO) {
    54→      console.log(message);
    55→    }
    56→
    57→    // Append to log file synchronously
    58→    try {
    59→      appendFileSync(this.logFile, fileMessage, 'utf-8');
    60→    } catch (error) {
    61→      console.error(`Failed to write to log file: ${error}`);
    62→    }
    63→  }
    64→
    65→  debug(message: string): void {
    66→    this.log(LogLevel.DEBUG, message);
    67→  }
    68→
    69→  info(message: string): void {
    70→    this.log(LogLevel.INFO, message);
    71→  }
    72→
    73→  warn(message: string): void {
    74→    this.log(LogLevel.WARN, message);
    75→  }
    76→
    77→  error(message: string): void {
    78→    this.log(LogLevel.ERROR, message);
    79→  }
    80→
    81→  getLogFile(): string {
    82→    return this.logFile;
    83→  }
    84→}
