   100→/**
   101→ * Set up logger that writes to both console and file using adw_id.
   102→ *
   103→ * Creates log directory: agents/{adw_id}/{trigger_type}/
   104→ * Log file path: agents/{adw_id}/{trigger_type}/execution.log
   105→ *
   106→ * @param adwId - The ADW workflow ID
   107→ * @param triggerType - Type of trigger (adw_plan_build, trigger_webhook, etc.)
   108→ * @returns Configured logger instance
   109→ */
   110→export async function setupLogger(
   111→  adwId: string,
   112→  triggerType: string = 'adw_plan_build'
   113→): Promise<Logger> {
   114→  // Get project root (assuming this file is in src/modules/)
   115→  const projectRoot = path.resolve(import.meta.dir, '../..');
   116→
   117→  // Create log directory: agents/{adw_id}/{trigger_type}/
   118→  const logDir = path.join(projectRoot, 'agents', adwId, triggerType);
   119→  await mkdir(logDir, { recursive: true });
   120→
   121→  // Log file path: agents/{adw_id}/{trigger_type}/execution.log
   122→  const logFile = path.join(logDir, 'execution.log');
   123→
   124→  // Create logger instance
   125→  const logger = new AdwLogger(adwId, logFile, triggerType);
   126→
   127→  // Store in registry
   128→  loggers.set(adwId, logger);
   129→
   130→  // Log initial setup message
   131→  logger.info(`ADW Logger initialized - ID: ${adwId}`);
   132→  logger.debug(`Log file: ${logFile}`);
   133→
   134→  return logger;
   135→}
