   137→/**
   138→ * Get existing logger by ADW ID.
   139→ *
   140→ * @param adwId - The ADW workflow ID
   141→ * @returns Logger instance
   142→ * @throws Error if logger not found
   143→ */
   144→export function getLogger(adwId: string): Logger {
   145→  const logger = loggers.get(adwId);
