   152→/**
   153→ * Parse JSON that may be wrapped in markdown code blocks (with schema validation).
   154→ *
   155→ * @param text - String containing JSON, possibly wrapped in markdown
   156→ * @param schema - Zod schema to validate/parse the result
   157→ * @returns Parsed JSON object validated with Zod schema
   158→ */
   159→export function parseJson<T extends z.ZodType>(text: string, schema: T): z.infer<T>;
   160→
   161→/**
   162→ * Parse JSON that may be wrapped in markdown code blocks (without schema validation).
   163→ *
   164→ * @param text - String containing JSON, possibly wrapped in markdown
   165→ * @returns Parsed JSON object
   166→ */
   167→export function parseJson(text: string): unknown;
   168→
   169→/**
   170→ * Parse JSON that may be wrapped in markdown code blocks.
   171→ *
   172→ * Handles various formats:
   173→ * - Raw JSON
   174→ * - JSON wrapped in ```json ... ```
   175→ * - JSON wrapped in ``` ... ```
   176→ * - JSON with extra whitespace or newlines
   177→ *
   178→ * @param text - String containing JSON, possibly wrapped in markdown
   179→ * @param schema - Optional Zod schema to validate/parse the result
   180→ * @returns Parsed JSON object, optionally validated with Zod schema
   181→ * @throws Error if JSON cannot be parsed or validation fails
   182→ *
   183→ * @example
   184→ * ```typescript
   185→ * const data = parseJson(text, MySchema);
   186→ * // data is fully typed as z.infer<typeof MySchema>
   187→ * ```
   188→ */
   189→export function parseJson<T extends z.ZodType>(text: string, schema?: T): unknown {
   190→  // Try to extract JSON from markdown code blocks
   191→  // Pattern matches ```json\n...\n``` or ```\n...\n```
   192→  const codeBlockPattern = /```(?:json)?\s*\n(.*?)\n```/s;
   193→  const match = text.match(codeBlockPattern);
   194→
   195→  let jsonStr: string;
   196→  if (match?.[1]) {
   197→    jsonStr = match[1].trim();
   198→  } else {
   199→    // No code block found, try to parse the entire text
   200→    jsonStr = text.trim();
   201→  }
   202→
   203→  // Try to find JSON array or object boundaries if not already clean
   204→  if (!jsonStr.startsWith('[') && !jsonStr.startsWith('{')) {
   205→    // Look for JSON array
   206→    const arrayStart = jsonStr.indexOf('[');
   207→    const arrayEnd = jsonStr.lastIndexOf(']');
   208→
   209→    // Look for JSON object
   210→    const objStart = jsonStr.indexOf('{');
   211→    const objEnd = jsonStr.lastIndexOf('}');
   212→
   213→    // Determine which comes first and extract accordingly
   214→    if (arrayStart !== -1 && (objStart === -1 || arrayStart < objStart)) {
   215→      if (arrayEnd !== -1) {
   216→        jsonStr = jsonStr.slice(arrayStart, arrayEnd + 1);
   217→      }
   218→    } else if (objStart !== -1) {
   219→      if (objEnd !== -1) {
   220→        jsonStr = jsonStr.slice(objStart, objEnd + 1);
   221→      }
   222→    }
   223→  }
   224→
   225→  try {
   226→    const result = JSON.parse(jsonStr);
   227→
   228→    // If schema is provided, validate with Zod
   229→    if (schema) {
   230→      return schema.parse(result);
   231→    }
   232→
   233→    return result;
   234→  } catch (error) {
   235→    const preview = jsonStr.slice(0, 200);
   236→    if (error instanceof z.ZodError) {
   237→      throw new Error(`Zod validation failed: ${error.message}. JSON was: ${preview}...`);
   238→    } else {
   239→      throw new Error(`Failed to parse JSON: ${error}. Text was: ${preview}...`);
   240→    }
   241→  }
   242→}
