   244→/**
   245→ * Check that required environment variables are set.
   246→ *
   247→ * @param requiredVars - Array of required environment variable names
   248→ * @param logger - Optional logger instance for error reporting
   249→ * @returns Object with missing variable names (empty array if all present)
   250→ *
   251→ * @example
   252→ * ```typescript
   253→ * const { missing } = checkEnvVars(["ANTHROPIC_API_KEY", "CLAUDE_CODE_PATH"], logger);
   254→ * if (missing.length > 0) {
   255→ *   process.exit(1);
   256→ * }
   257→ * ```
   258→ */
   259→export function checkEnvVars(
   260→  requiredVars: string[] = ['ANTHROPIC_API_KEY', 'CLAUDE_CODE_PATH'],
   261→  logger?: Logger
   262→): { missing: string[] } {
   263→  const missing = requiredVars.filter((varName) => !process.env[varName]);
   264→
   265→  if (missing.length > 0) {
   266→    const errorMsg = 'Error: Missing required environment variables:';
   267→    if (logger) {
   268→      logger.error(errorMsg);
   269→      missing.forEach((varName) => logger.error(`  - ${varName}`));
   270→    } else {
   271→      console.error(errorMsg);
   272→      missing.forEach((varName) => console.error(`  - ${varName}`));
   273→    }
   274→  }
   275→
   276→  return { missing };
   277→}
