   279→/**
   280→ * Format a status message for agent operations with visibility into ADW ID and branch.
   281→ *
   282→ * Note: Rich markup (bold, colors) is stripped in Bun console output for simplicity.
   283→ * Use ANSI codes if terminal formatting is required.
   284→ *
   285→ * @param action - The action being performed (e.g., "Building solution", "Creating plan")
   286→ * @param adwId - The ADW ID for tracking
   287→ * @param worktree - The worktree/branch name
   288→ * @param phase - Optional phase name (e.g., "build", "plan", "implement")
   289→ * @returns Formatted status message with ADW ID and branch visibility
   290→ */
   291→export function formatAgentStatus(
   292→  action: string,
   293→  adwId: string,
   294→  worktree: string,
   295→  phase?: string
   296→): string {
   297→  // Format the ADW ID (first 6 chars for brevity)
   298→  const shortId = adwId.length > 6 ? adwId.slice(0, 6) : adwId;
   299→
   300→  // Build the status components (simplified, no rich markup)
   301→  let statusMsg = `${action} (${shortId}@${worktree}`;
   302→
   303→  if (phase) {
   304→    statusMsg += ` • ${phase}`;
   305→  }
   306→
   307→  statusMsg += ')';
   308→
   309→  return statusMsg;
   310→}
