   312→/**
   313→ * Format a status message specifically for worktree operations.
   314→ *
   315→ * @param action - The action being performed (e.g., "Creating", "Initializing")
   316→ * @param worktree - The worktree name
   317→ * @param adwId - Optional ADW ID for tracking
   318→ * @returns Formatted status message for worktree operations
   319→ */
   320→export function formatWorktreeStatus(action: string, worktree: string, adwId?: string): string {
   321→  let baseMsg = `${action} worktree '${worktree}'`;
   322→
   323→  if (adwId) {
   324→    const shortId = adwId.length > 6 ? adwId.slice(0, 6) : adwId;
   325→    baseMsg += ` (${shortId})`;
   326→  }
   327→
   328→  return baseMsg;
   329→}
