   331→/**
   332→ * Get filtered environment variables safe for subprocess execution.
   333→ *
   334→ * Returns only the environment variables needed for ADW workflows based on
   335→ * .env.sample configuration. This prevents accidental exposure of sensitive
   336→ * credentials to subprocesses.
   337→ *
   338→ * @returns Object containing only required environment variables
   339→ */
   340→export function getSafeSubprocessEnv(): Record<string, string> {
   341→  const safeEnvVars: Record<string, string | undefined> = {
   342→    // Anthropic Configuration (required)
   343→    ANTHROPIC_API_KEY: process.env.ANTHROPIC_API_KEY,
   344→
   345→    // GitHub Configuration (optional)
   346→    // GITHUB_PAT is optional - if not set, will use default gh auth
   347→    GITHUB_PAT: process.env.GITHUB_PAT,
   348→
   349→    // Claude Code Configuration
   350→    CLAUDE_CODE_PATH: process.env.CLAUDE_CODE_PATH || 'claude',
   351→    CLAUDE_BASH_MAINTAIN_PROJECT_WORKING_DIR:
   352→      process.env.CLAUDE_BASH_MAINTAIN_PROJECT_WORKING_DIR || 'true',
   353→
   354→    // Agent Cloud Sandbox Environment (optional)
   355→    E2B_API_KEY: process.env.E2B_API_KEY,
   356→
   357→    // Cloudflare tunnel token (optional)
   358→    CLOUDFLARED_TUNNEL_TOKEN: process.env.CLOUDFLARED_TUNNEL_TOKEN,
   359→
   360→    // Essential system environment variables
   361→    HOME: process.env.HOME,
   362→    USER: process.env.USER,
   363→    PATH: process.env.PATH,
   364→    SHELL: process.env.SHELL,
   365→    TERM: process.env.TERM,
   366→    LANG: process.env.LANG,
   367→    LC_ALL: process.env.LC_ALL,
   368→
   369→    // Node/Bun-specific variables that subprocesses might need
   370→    NODE_ENV: process.env.NODE_ENV,
   371→    BUN_INSTALL: process.env.BUN_INSTALL,
   372→
   373→    // Working directory tracking
   374→    PWD: process.cwd(),
   375→  };
   376→
   377→  // Add GH_TOKEN as alias for GITHUB_PAT if it exists
   378→  const githubPat = process.env.GITHUB_PAT;
   379→  if (githubPat) {
   380→    safeEnvVars.GH_TOKEN = githubPat;
   381→  }
   382→
   383→  // Filter out undefined values
   384→  return Object.fromEntries(
   385→    Object.entries(safeEnvVars).filter(([_, v]) => v !== undefined)
   386→  ) as Record<string, string>;
   387→}
