   389→/**
   390→ * Truncate a string to a maximum length, adding ellipsis if truncated.
   391→ *
   392→ * @param text - The text to truncate
   393→ * @param maxLength - Maximum length (default: 100)
   394→ * @returns Truncated string with ellipsis if needed
   395→ */
   396→export function truncate(text: string, maxLength: number = 100): string {
   397→  if (text.length <= maxLength) {
   398→    return text;
   399→  }
   400→  return text.slice(0, maxLength - 3) + '...';
   401→}
