   423→/**
   424→ * Format a duration in milliseconds to a human-readable string.
   425→ *
   426→ * @param ms - Duration in milliseconds
   427→ * @returns Formatted duration string (e.g., "2.5s", "1m 30s", "1h 15m")
   428→ */
   429→export function formatDuration(ms: number): string {
   430→  const seconds = Math.floor(ms / 1000);
   431→  const minutes = Math.floor(seconds / 60);
   432→  const hours = Math.floor(minutes / 60);
   433→
   434→  if (hours > 0) {
   435→    const remainingMinutes = minutes % 60;
   436→    return `${hours}h ${remainingMinutes}m`;
   437→  } else if (minutes > 0) {
   438→    const remainingSeconds = seconds % 60;
   439→    return `${minutes}m ${remainingSeconds}s`;
   440→  } else if (seconds > 0) {
   441→    const remainingMs = ms % 1000;
   442→    const decimalSeconds = seconds + remainingMs / 1000;
   443→    return `${decimalSeconds.toFixed(1)}s`;
   444→  } else {
   445→    return `${ms}ms`;
   446→  }
   447→}
