    59→  }
    60→
    61→  // Transform and group tasks
    62→  for (const apiTask of apiTasks) {
    63→    const stageName = apiTask.workflowColumn?.name || "Backlog";
    64→    if (!stageMap.has(stageName)) {
    65→      stageMap.set(stageName, []);
    66→    }
    67→
    68→    // Find time entries for this task
    69→    const taskTimelogs = timeEntries
    70→      .filter((te) => te.taskId === apiTask.id)
    71→      .map((te) => ({
    72→        id: `tl-${te.id}`,
    73→        hours: te.minutes / 60,
    74→        date: te.date,
    75→        isBillable: te.isBillable ?? true,
    76→        comment: te.description || "",
    77→      }));
    78→
    79→    const task: Task = {
    80→      id: `task-${apiTask.id}`,
    81→      title: apiTask.name,
    82→      description: apiTask.description,
    83→      priority: (apiTask.priority as "low" | "medium" | "high") || "medium",
    84→      tags: apiTask.tags?.map((t) => t.name || "").filter(Boolean) || [],
    85→      assignedTo: `https://picsum.photos/seed/${apiTask.id}/32/32`,
    86→      timeLogs: taskTimelogs,
    87→      comments: [],
    88→    };
    89→
    90→    stageMap.get(stageName)!.push(task);
    91→  }
    92→
    93→  // Convert to Stage array
    94→  const result: Stage[] = [];
    95→  let stageIdx = 0;
    96→  for (const [name, tasks] of stageMap) {
    97→    if (tasks.length > 0 || stages.some((s) => s.name === name)) {
    98→      result.push({
    99→        id: `stage-${stageIdx++}`,
   100→        name,
   101→        tasks,
   102→      });
   103→    }
   104→  }
   105→
   106→  return result;
   107→}
