   591→  // Handle custom visualization request from input - sends prompt to AI for interpretation
   592→  const handleVisualizationRequest = async (prompt: string) => {
   593→    console.log("Visualization request:", prompt);
   594→    setIsChartLoading(true);
   595→    
   596→    const numericProjectId = activeProjectId 
   597→      ? parseInt(activeProjectId.replace('proj-', '')) 
   598→      : undefined;
   599→
   600→    try {
   601→      const response = await fetch(apiUrl("/api/agent/visualize"), {
   602→        method: "POST",
   603→        headers: {
   604→          "Content-Type": "application/json",
   605→        },
   606→        body: JSON.stringify({
   607→          prompt,
   608→          projectId: numericProjectId,
   609→        }),
   610→      });
   611→
   612→      if (!response.ok) {
   613→        throw new Error(`Visualization request error: ${response.status}`);
   614→      }
   615→
   616→      const reader = response.body?.getReader();
   617→      if (!reader) {
   618→        throw new Error("No response body");
   619→      }
   620→
   621→      const decoder = new TextDecoder();
   622→
   623→      while (true) {
   624→        const { done, value } = await reader.read();
   625→        if (done) break;
   626→
   627→        const chunk = decoder.decode(value);
   628→        const lines = chunk.split("\n").filter(line => line.startsWith("data: "));
   629→
   630→        for (const line of lines) {
   631→          const data = line.slice(6);
   632→          
   633→          if (data === "[DONE]") {
   634→            break;
   635→          }
   636→
   637→          try {
   638→            const parsed = JSON.parse(data);
   639→            
   640→            if (parsed.type === 'visualization' && parsed.spec) {
   641→              // Replace with new visualization (don't accumulate for custom requests)
   642→              setDisplayData((prev) => {
   643→                const newItems: DisplayItem[] = [];
   644→                
   645→                if (parsed.spec.type === "chart" && parsed.spec.data) {
   646→                  newItems.push({
   647→                    id: `chart-${parsed.spec.title || 'main'}-${Date.now()}`,
   648→                    type: "chart",
   649→                    data: {
   650→                      chartType: parsed.spec.chartType || "bar",
   651→                      title: parsed.spec.title || "Chart",
   652→                      data: parsed.spec.data,
   653→                      summary: parsed.spec.summary,
   654→                    },
   655→                  });
   656→                } else if (parsed.spec.type === "custom" && parsed.spec.svg) {
