  1155→  const handleLogWork = async (
  1156→    taskName: string | undefined,
  1157→    hours: number,
  1158→    comment: string,
  1159→    isBillable: boolean
  1160→  ): Promise<boolean> => {
  1161→    let targetProject = activeProject;
  1162→    let targetTask: Task | null = null;
  1163→    let targetStage: Stage | null = null;
  1164→
  1165→    const findTaskInProject = (proj: Project, name: string) => {
  1166→      for (const stage of proj.stages) {
  1167→        const task = stage.tasks.find((t) =>
  1168→          t.title.toLowerCase().includes(name.toLowerCase())
  1169→        );
  1170→        if (task) return { task, stage };
  1171→      }
  1172→      return null;
  1173→    };
  1174→
  1175→    if (activeProject && taskName) {
  1176→      const found = findTaskInProject(activeProject, taskName);
  1177→      if (found) {
  1178→        targetTask = found.task;
  1179→        targetStage = found.stage;
  1180→      }
  1181→    } else if (!activeProject && taskName) {
  1182→      for (const proj of projects) {
  1183→        const found = findTaskInProject(proj, taskName);
  1184→        if (found) {
  1185→          targetTask = found.task;
  1186→          targetStage = found.stage;
  1187→          targetProject = proj;
  1188→          break;
  1189→        }
  1190→      }
  1191→    } else if (activeProject && !taskName) {
  1192→      if (activeProject.stages[0]?.tasks.length > 0) {
  1193→        targetTask = activeProject.stages[0].tasks[0];
  1194→        targetStage = activeProject.stages[0];
  1195→      }
  1196→    }
  1197→
  1198→    if (targetTask && targetStage && targetProject) {
  1199→      // Extract numeric task ID from the task.id (format: "task-{number}")
  1200→      const taskIdMatch = targetTask.id.match(/task-(\d+)/);
  1201→      const numericTaskId = taskIdMatch ? parseInt(taskIdMatch[1]) : null;
  1202→
  1203→      // Try to log to real API if we have a numeric task ID
  1204→      if (numericTaskId && activeProjectId) {
  1205→        try {
  1206→          await teamworkService.logTime(numericTaskId, hours, comment, isBillable);
  1207→          // Refresh the project data to show updated time entries
  1208→          loadProjectDetails(activeProjectId);
  1209→          return true;
  1210→        } catch (err) {
  1211→          console.error("Failed to log time to API:", err);
  1212→          // Fall through to local update
  1213→        }
  1214→      }
  1215→
  1216→      // Local update (fallback or for mock tasks)
  1217→      const updatedTask = { ...targetTask };
  1218→
  1219→      updatedTask.timeLogs = [
  1220→        ...(updatedTask.timeLogs || []),
  1221→        {
  1222→          id: `log-${Date.now()}`,
  1223→          hours,
  1224→          comment,
  1225→          isBillable,
  1226→          date: new Date().toISOString(),
  1227→        },
  1228→      ];
  1229→
  1230→      updatedTask.comments = [
  1231→        ...(updatedTask.comments || []),
  1232→        {
  1233→          id: `cmt-${Date.now()}`,
  1234→          text: `Logged ${hours}h: ${comment}`,
  1235→          createdAt: new Date().toISOString(),
  1236→          author: "AI",
  1237→        },
  1238→      ];
  1239→
  1240→      const updatedProject = {
  1241→        ...targetProject,
  1242→        stages: targetProject.stages.map((s) =>
  1243→          s.id === targetStage!.id
  1244→            ? {
  1245→                ...s,
  1246→                tasks: s.tasks.map((t) =>
  1247→                  t.id === updatedTask.id ? updatedTask : t
  1248→                ),
  1249→              }
  1250→            : s
  1251→        ),
  1252→      };
  1253→
  1254→      setProjects((prev) =>
  1255→        prev.map((p) => (p.id === updatedProject.id ? updatedProject : p))
  1256→      );
  1257→      return true;
  1258→    }
  1259→
  1260→    return false;
  1261→  };
