/**
 * Teamwork API Client
 *
 * A comprehensive TypeScript client for Teamwork.com API v3.
 * Provides direct API access with workflow and stage support.
 *
 * @example
 * ```typescript
 * import { createTaskMonitor } from 'teamwork-api-client';
 *
 * const monitor = createTaskMonitor();
 *
 * // Get eligible tasks
 * const tasks = await monitor.getEligibleTasks(projectId, ['new', 'pending'], 10);
 *
 * // Claim and process task
 * await monitor.claimTask(taskId, adwId, { model: 'sonnet' });
 *
 * // Move task to stage
 * await monitor.moveTaskToStage(taskId, workflowId, 'In Progress');
 *
 * // Complete task
 * await monitor.completeTask(taskId, adwId, { commitHash: 'abc123' });
 * ```
 */

// Core client
import { TeamworkHttpClient as HttpClient, createClientFromEnv } from './client.ts';
export {
  TeamworkHttpClient,
  createClientFromEnv,
  type TeamworkClientConfig,
  type RequestOptions,
  type ApiError,
} from './client.ts';

// Type definitions
export * from './types.ts';

// Resource modules
import { TasksResource as Tasks } from './resources/tasks.ts';
import { WorkflowsResource as Workflows } from './resources/workflows.ts';
import { ProjectsResource as Projects } from './resources/projects.ts';
import { CommentsResource as Comments } from './resources/comments.ts';

export { TasksResource, type ListTasksOptions, type CreateTaskOptions, type UpdateTaskOptions } from './resources/tasks.ts';

export { WorkflowsResource, type ListWorkflowsOptions, type ListStageTasksOptions } from './resources/workflows.ts';

export { ProjectsResource, type ListProjectsOptions } from './resources/projects.ts';

export {
  CommentsResource,
  type ListCommentsOptions,
  type CreateCommentOptions,
  type AdwStatusUpdate,
} from './resources/comments.ts';

// High-level facade
export {
  TeamworkTaskMonitor,
  createTaskMonitor,
  type TaskMonitorConfig,
  // Tag/trigger parsing utilities
  extractInlineTags,
  parseNativeTags,
  detectExecutionTrigger,
  cleanTaskDescription,
  getTaskPromptForAgent,
} from './task-monitor.ts';

/**
 * Create a fully configured Teamwork API client.
 *
 * @example
 * ```typescript
 * const client = createTeamworkClient({
 *   apiUrl: 'https://your-site.teamwork.com',
 *   bearerToken: 'your-token',
 * });
 *
 * // Use individual resources
 * const tasks = await client.tasks.list({ statuses: ['new'] });
 * const workflows = await client.workflows.list({ include: ['stages'] });
 * ```
 */
export function createTeamworkClient(config: {
  apiUrl: string;
  bearerToken: string;
  debug?: boolean;
}) {
  const httpClient = new HttpClient(config);

  return {
    http: httpClient,
    tasks: new Tasks(httpClient),
    workflows: new Workflows(httpClient),
    projects: new Projects(httpClient),
    comments: new Comments(httpClient),
  };
}

content.content:
     1→/**
     2→ * High-level facade for task monitoring and management.
     3→ * Provides drop-in replacement for MCP-based /get_teamwork_tasks and /update_teamwork_task.
     4→ */
     5→
     6→import { TeamworkHttpClient, createClientFromEnv, type TeamworkClientConfig } from './client.ts';
     7→import { TasksResource } from './resources/tasks.ts';
     8→import { WorkflowsResource } from './resources/workflows.ts';
     9→import { ProjectsResource } from './resources/projects.ts';
    10→import { CommentsResource, type AdwStatusUpdate } from './resources/comments.ts';
    11→import { type ApiTask, type ProcessedTask, ProcessedTaskSchema } from './types.ts';
    12→
    13→// ============================================================================
    14→// Tag & Trigger Parsing
    15→// ============================================================================
    16→
    17→/**
    18→ * Extract inline {{key: value}} tags from description.
    19→ */
    20→export function extractInlineTags(description: string): Record<string, string> {
    21→  const pattern = /\{\{(\w+):\s*([^}]+)\}\}/g;
    22→  const tags: Record<string, string> = {};
    23→  let match: RegExpExecArray | null;
    24→
    25→  while ((match = pattern.exec(description)) !== null) {
    26→    const [, key, value] = match;
    27→    if (key && value) {
    28→      tags[key.toLowerCase()] = value.trim();
    29→    }
    30→  }
    31→
    32→  return tags;
    33→}
    34→
    35→/**
    36→ * Parse native Teamwork tags (e.g., "prototype:vite_vue") into key-value pairs.
    37→ * Note: Tags may have optional `name` field when returned as references.
    38→ */
    39→export function parseNativeTags(
    40→  tags: Array<{ id: number; name?: string }>
    41→): Record<string, string> {
    42→  const result: Record<string, string> = {};
    43→
    44→  for (const tag of tags) {
    45→    // Skip tags without names (reference-only)
    46→    if (!tag.name) continue;
    47→
    48→    // Parse "key:value" format
    49→    const colonIndex = tag.name.indexOf(':');
    50→    if (colonIndex !== -1) {
    51→      const key = tag.name.slice(0, colonIndex).toLowerCase().trim();
    52→      const value = tag.name.slice(colonIndex + 1).trim();
    53→      result[key] = value;
    54→    }
    55→  }
    56→
    57→  return result;
    58→}
    59→
    60→/**
    61→ * Detect execution trigger from description.
    62→ * Returns 'execute', 'continue', or null.
    63→ */
    64→export function detectExecutionTrigger(description: string): {
    65→  trigger: 'execute' | 'continue' | null;
    66→  continuePrompt?: string;
    67→} {
    68→  const trimmed = description.trim();
    69→
    70→  // Check for "continue - prompt" pattern
    71→  const continueMatch = trimmed.match(/continue\s*-\s*(.+)$/is);
    72→  if (continueMatch?.[1]) {
    73→    return {
    74→      trigger: 'continue',
    75→      continuePrompt: continueMatch[1].trim(),
    76→    };
    77→  }
    78→
    79→  // Check for "execute" at end of description
    80→  if (/execute\s*$/i.test(trimmed)) {
    81→    return { trigger: 'execute' };
    82→  }
    83→
    84→  return { trigger: null };
    85→}
    86→
    87→/**
    88→ * Clean task description by removing tags and triggers.
    89→ */
    90→export function cleanTaskDescription(description: string): string {
    91→  let cleaned = description;
    92→
    93→  // Remove inline tags
    94→  cleaned = cleaned.replace(/\{\{[^}]+\}\}/g, '');
    95→
    96→  // Remove "execute" trigger
    97→  cleaned = cleaned.replace(/execute\s*$/gi, '');
    98→
    99→  // Remove "continue - prompt" trigger
   100→  cleaned = cleaned.replace(/continue\s*-\s*.+$/gis, '');
   101→
   102→  // Clean up whitespace
   103→  cleaned = cleaned.trim();
   104→
   105→  return cleaned;
   106→}
   107→
   108→/**
   109→ * Get clean task prompt for agent processing.
   110→ */
   111→export function getTaskPromptForAgent(
   112→  description: string,
   113→  executionTrigger: 'execute' | 'continue' | null,
   114→  continuePrompt?: string
   115→): string {
   116→  if (executionTrigger === 'continue' && continuePrompt) {
   117→    return continuePrompt;
   118→  }
   119→  return cleanTaskDescription(description);
   120→}
   121→
   122→// ============================================================================
   123→// Task Monitor Facade
   124→// ============================================================================
   125→
   126→export interface TaskMonitorConfig extends Partial<TeamworkClientConfig> {
   127→  /** Default project ID for operations */
   128→  defaultProjectId?: number;
   129→  /** Status mapping from system status to Teamwork status */
   130→  statusMapping?: Record<string, string>;
   131→}
   132→
   133→/**
   134→ * High-level task monitor for Teamwork integration.
   135→ */
   136→export class TeamworkTaskMonitor {
   137→  private readonly client: TeamworkHttpClient;
   138→  readonly tasks: TasksResource;
   139→  readonly workflows: WorkflowsResource;
   140→  readonly projects: ProjectsResource;
   141→  readonly comments: CommentsResource;
   142→
   143→  private readonly defaultProjectId?: number;
   144→  private readonly statusMapping: Record<string, string>;
   145→
   146→  constructor(config?: TaskMonitorConfig) {
   147→    // Create client from config or environment
   148→    if (config?.apiUrl && config?.bearerToken) {
   149→      this.client = new TeamworkHttpClient({
   150→        apiUrl: config.apiUrl,
   151→        bearerToken: config.bearerToken,
   152→        debug: config.debug,
   153→      });
   154→    } else {
   155→      this.client = createClientFromEnv(config?.debug);
   156→    }
   157→
   158→    // Initialize resource modules
   159→    this.tasks = new TasksResource(this.client);
   160→    this.workflows = new WorkflowsResource(this.client);
   161→    this.projects = new ProjectsResource(this.client);
   162→    this.comments = new CommentsResource(this.client);
   163→
   164→    // Configuration
   165→    this.defaultProjectId = config?.defaultProjectId;
   166→    this.statusMapping = config?.statusMapping ?? {
   167→      'Not started': 'new',
   168→      'In progress': 'active',
   169→      Done: 'completed',
   170→      'HIL Review': 'pending',
   171→      Failed: 'pending',
   172→    };
   173→  }
   174→
   175→  /**
   176→   * Convert API task to processed task format (compatible with existing TeamworkTask schema).
   177→   */
   178→  processTask(apiTask: ApiTask, projectId: number): ProcessedTask {
   179→    const description = apiTask.description ?? '';
   180→
   181→    // Parse tags from native tags and inline tags
   182→    const nativeTags = parseNativeTags(apiTask.tags ?? []);
   183→    const inlineTags = extractInlineTags(description);
   184→
   185→    // Merge tags (native takes precedence)
   186→    const tags = { ...inlineTags, ...nativeTags };
   187→
   188→    // Detect execution trigger
   189→    const { trigger, continuePrompt } = detectExecutionTrigger(description);
   190→
   191→    // Get task prompt
   192→    const taskPrompt = getTaskPromptForAgent(description, trigger, continuePrompt);
   193→
   194→    // Get assignee info - handle both object and array formats
   195→    let assignedTo: string | null = null;
   196→    if (apiTask.assignees) {
   197→      if (Array.isArray(apiTask.assignees)) {
   198→        // Array format: [{ id: number, type?: string }]
   199→        const firstUser = apiTask.assignees.find((a) => a.type === 'users' || !a.type);
   200→        assignedTo = firstUser?.id?.toString() ?? null;
   201→      } else {
   202→        // Object format: { userIds?: number[], teamIds?: number[], companyIds?: number[] }
   203→        assignedTo = apiTask.assignees.userIds?.[0]?.toString() ?? null;
   204→      }
   205→    }
   206→
   207→    return ProcessedTaskSchema.parse({
   208→      task_id: String(apiTask.id),
   209→      project_id: String(projectId),
   210→      title: apiTask.name,
   211→      status: apiTask.status ?? 'new',
   212→      description,
   213→      tags,
   214→      worktree: tags['worktree'],
   215→      model: tags['model'],
   216→      workflow_type: tags['workflow'],
   217→      prototype: tags['prototype'],
   218→      execution_trigger: trigger ?? undefined,
   219→      task_prompt: taskPrompt,
   220→      assigned_to: assignedTo,
   221→      created_time: apiTask.createdAt,
   222→      last_edited_time: apiTask.updatedAt,
   223→      due_date: apiTask.dueDate ?? null,
   224→      priority: apiTask.priority,
   225→      estimated_minutes: apiTask.estimatedMinutes,
   226→      stage_id: apiTask.workflowColumn?.id,
   227→      stage_name: apiTask.workflowColumn?.name,
   228→    });
   229→  }
   230→
   231→  /**
   232→   * Get eligible tasks for processing (replaces /get_teamwork_tasks).
   233→   */
   234→  async getEligibleTasks(
   235→    projectId?: number,
   236→    statusFilter: string[] = ['new', 'pending'],
   237→    limit: number = 10
   238→  ): Promise<ProcessedTask[]> {
   239→    const pid = projectId ?? this.defaultProjectId;
   240→    if (!pid) {
   241→      throw new Error('Project ID is required');
   242→    }
   243→
   244→    // Fetch tasks with status filter
   245→    const response = await this.tasks.listByProject(pid, {
   246→      statuses: statusFilter,
   247→      include: ['tags', 'assignees'],
   248→      pageSize: limit,
   249→    });
   250→
   251→    // Process and filter eligible tasks
   252→    const eligibleTasks: ProcessedTask[] = [];
   253→
   254→    for (const apiTask of response.tasks) {
   255→      const processed = this.processTask(apiTask, pid);
   256→
   257→      // Check if task has valid execution trigger
   258→      if (processed.execution_trigger === 'execute' || processed.execution_trigger === 'continue') {
   259→        eligibleTasks.push(processed);
   260→      }
   261→    }
   262→
