/**
 * Tasks resource module for Teamwork API.
 * Provides CRUD operations and filtering for tasks.
 */

import type { TeamworkHttpClient } from '../client.ts';
import {
  type ApiTask,
  type TaskListResponse,
  type TaskResponse,
  type CreateTaskRequest,
  type UpdateTaskRequest,
  TaskListResponseSchema,
  TaskResponseSchema,
} from '../types.ts';

export interface ListTasksOptions {
  /** Filter by status (e.g., 'new', 'in progress', 'completed') */
  statuses?: string[];
  /** Filter by tag IDs */
  tagIds?: number[];
  /** Filter by assignee user IDs */
  assigneeUserIds?: number[];
  /** Filter by assignee team IDs */
  assigneeTeamIds?: number[];
  /** Include related data */
  include?: ('tags' | 'assignees' | 'users' | 'teams')[];
  /** Page number */
  page?: number;
  /** Items per page */
  pageSize?: number;
  /** Order by field */
  orderBy?: string;
  /** Order direction */
  orderMode?: 'asc' | 'desc';
  /** Filter by updated after date */
  updatedAfter?: string;
  /** Include completed tasks */
  includeCompletedTasks?: boolean;
  /** Search term */
  searchTerm?: string;
}

export interface CreateTaskOptions {
  name: string;
  description?: string;
  priority?: 'none' | 'low' | 'medium' | 'high';
  startDate?: string;
  dueDate?: string;
  estimatedMinutes?: number;
  assigneeUserIds?: number[];
  assigneeTeamIds?: number[];
  tagIds?: number[];
}

export interface UpdateTaskOptions {
  name?: string;
  description?: string;
  status?: string;
  priority?: 'none' | 'low' | 'medium' | 'high';
  progress?: number;
  startDate?: string | null;
  dueDate?: string | null;
  estimatedMinutes?: number;
  assigneeUserIds?: number[];
  assigneeTeamIds?: number[];
  tagIds?: number[];
}

/**
 * Tasks resource for Teamwork API.
 */
export class TasksResource {
  constructor(private readonly client: TeamworkHttpClient) {}

  /**
   * List all tasks across all projects.
   */
  async list(options?: ListTasksOptions): Promise<TaskListResponse> {
    const params: Record<string, string | number | boolean | string[] | undefined> = {};

    if (options?.statuses?.length) {
      params['taskStatuses'] = options.statuses.join(',');
    }
    if (options?.tagIds?.length) {
      params['tagIds'] = options.tagIds.join(',');
    }
    if (options?.assigneeUserIds?.length) {
      params['assignedToUserIds'] = options.assigneeUserIds.join(',');
    }
    if (options?.assigneeTeamIds?.length) {
      params['assignedToTeamIds'] = options.assigneeTeamIds.join(',');
    }
    if (options?.include?.length) {
      params['include'] = options.include;
    }
    if (options?.page !== undefined) {
      params['page'] = options.page;
    }
    if (options?.pageSize !== undefined) {
      params['pageSize'] = options.pageSize;
    }
    if (options?.orderBy) {
      params['orderBy'] = options.orderBy;
    }
    if (options?.orderMode) {
      params['orderMode'] = options.orderMode;
    }
    if (options?.updatedAfter) {
      params['updatedAfter'] = options.updatedAfter;
    }
    if (options?.includeCompletedTasks !== undefined) {
      params['includeCompletedTasks'] = options.includeCompletedTasks;
    }
    if (options?.searchTerm) {
      params['searchTerm'] = options.searchTerm;
    }

    const response = await this.client.get<TaskListResponse>('/projects/api/v3/tasks.json', params);
    return TaskListResponseSchema.parse(response);
  }

  /**
   * List tasks for a specific project.
   */
  async listByProject(projectId: number, options?: ListTasksOptions): Promise<TaskListResponse> {
    const params: Record<string, string | number | boolean | string[] | undefined> = {};

    if (options?.statuses?.length) {
      params['taskStatuses'] = options.statuses.join(',');
    }
    if (options?.tagIds?.length) {
      params['tagIds'] = options.tagIds.join(',');
    }
    if (options?.assigneeUserIds?.length) {
      params['assignedToUserIds'] = options.assigneeUserIds.join(',');
    }
    if (options?.assigneeTeamIds?.length) {
      params['assignedToTeamIds'] = options.assigneeTeamIds.join(',');
    }
    if (options?.include?.length) {
      params['include'] = options.include;
    }
    if (options?.page !== undefined) {
      params['page'] = options.page;
    }
    if (options?.pageSize !== undefined) {
      params['pageSize'] = options.pageSize;
    }
    if (options?.orderBy) {
      params['orderBy'] = options.orderBy;
    }
    if (options?.orderMode) {
      params['orderMode'] = options.orderMode;
    }
    if (options?.updatedAfter) {
      params['updatedAfter'] = options.updatedAfter;
    }
    if (options?.includeCompletedTasks !== undefined) {
      params['includeCompletedTasks'] = options.includeCompletedTasks;
    }
    if (options?.searchTerm) {
      params['searchTerm'] = options.searchTerm;
    }

    const response = await this.client.get<TaskListResponse>(
      `/projects/api/v3/projects/${projectId}/tasks.json`,
      params
    );
    return TaskListResponseSchema.parse(response);
  }

  /**
   * List tasks for a specific tasklist.
   */
  async listByTasklist(tasklistId: number, options?: ListTasksOptions): Promise<TaskListResponse> {
    const params: Record<string, string | number | boolean | string[] | undefined> = {};

    if (options?.statuses?.length) {
      params['taskStatuses'] = options.statuses.join(',');
    }
    if (options?.include?.length) {
      params['include'] = options.include;
    }
    if (options?.page !== undefined) {
      params['page'] = options.page;
    }
    if (options?.pageSize !== undefined) {
      params['pageSize'] = options.pageSize;
    }

    const response = await this.client.get<TaskListResponse>(
      `/projects/api/v3/tasklists/${tasklistId}/tasks.json`,
      params
    );
    return TaskListResponseSchema.parse(response);
  }

  /**
   * Get a single task by ID.
   */
  async get(taskId: number, include?: ('tags' | 'assignees' | 'users' | 'teams')[]): Promise<ApiTask> {
    const params: Record<string, string | string[] | undefined> = {};

    if (include?.length) {
      params['include'] = include;
    }

    const response = await this.client.get<TaskResponse>(`/projects/api/v3/tasks/${taskId}.json`, params);
    const parsed = TaskResponseSchema.parse(response);
    return parsed.task;
  }

  /**
   * Create a new task in a tasklist.
   */
  async create(tasklistId: number, options: CreateTaskOptions): Promise<ApiTask> {
    const body: CreateTaskRequest = {
      task: {
        name: options.name,
        description: options.description,
        priority: options.priority,
        startDate: options.startDate,
        dueDate: options.dueDate,
        estimatedMinutes: options.estimatedMinutes,
        tagIds: options.tagIds,
      },
    };

    if (options.assigneeUserIds?.length || options.assigneeTeamIds?.length) {
      body.task.assignees = {
        userIds: options.assigneeUserIds,
        teamIds: options.assigneeTeamIds,
      };
    }

    const response = await this.client.post<TaskResponse>(
      `/projects/api/v3/tasklists/${tasklistId}/tasks.json`,
      body
    );
    const parsed = TaskResponseSchema.parse(response);
    return parsed.task;
  }

  /**
   * Update an existing task.
   */
  async update(taskId: number, options: UpdateTaskOptions): Promise<ApiTask> {
    const body: UpdateTaskRequest = {
      task: {},
    };

    if (options.name !== undefined) body.task.name = options.name;
    if (options.description !== undefined) body.task.description = options.description;
    if (options.status !== undefined) body.task.status = options.status;
    if (options.priority !== undefined) body.task.priority = options.priority;
    if (options.progress !== undefined) body.task.progress = options.progress;
    if (options.startDate !== undefined) body.task.startDate = options.startDate;
    if (options.dueDate !== undefined) body.task.dueDate = options.dueDate;
    if (options.estimatedMinutes !== undefined) body.task.estimatedMinutes = options.estimatedMinutes;
    if (options.tagIds !== undefined) body.task.tagIds = options.tagIds;

    if (options.assigneeUserIds !== undefined || options.assigneeTeamIds !== undefined) {
      body.task.assignees = {
        userIds: options.assigneeUserIds,
        teamIds: options.assigneeTeamIds,
      };
    }

    const response = await this.client.patch<TaskResponse>(`/projects/api/v3/tasks/${taskId}.json`, body);
    const parsed = TaskResponseSchema.parse(response);
    return parsed.task;
  }

  /**
   * Delete a task.
   */
  async delete(taskId: number): Promise<void> {
    await this.client.delete(`/projects/api/v3/tasks/${taskId}.json`);
  }

  /**
   * Complete a task.
   */
  async complete(taskId: number): Promise<ApiTask> {
    return this.update(taskId, { status: 'completed' });
  }

  /**
   * Reopen a completed task.
   */
  async reopen(taskId: number): Promise<ApiTask> {
    return this.update(taskId, { status: 'reopened' });
  }
}

/**
 * Workflows resource module for Teamwork API.
 * Provides workflow and stage management operations.
 */

import type { TeamworkHttpClient } from '../client.ts';
import {
  type Workflow,
  type Stage,
  type WorkflowListResponse,
  type WorkflowResponse,
  type StageListResponse,
  type StageResponse,
  type TaskListResponse,
  type UpdateTaskPositionRequest,
  type AddTaskToStageRequest,
  WorkflowListResponseSchema,
  WorkflowResponseSchema,
  StageListResponseSchema,
  StageResponseSchema,
  TaskListResponseSchema,
} from '../types.ts';

export interface ListWorkflowsOptions {
  /** Filter by project IDs */
  projectIds?: number[];
  /** Filter by workflow IDs */
  workflowIds?: number[];
  /** Filter by stage names */
  stageNames?: string[];
  /** Search term */
  searchTerm?: string;
  /** Include related data */
  include?: ('stages' | 'projects' | 'users' | 'teams' | 'companies')[];
  /** Only return default workflow */
  onlyDefaultWorkflow?: boolean;
  /** Include archived workflows */
  includeArchived?: boolean;
  /** Include total count in response */
  includeTotalCount?: boolean;
  /** Page number */
  page?: number;
  /** Items per page */
  pageSize?: number;
}

export interface ListStageTasksOptions {
  /** Include related data */
  include?: ('tags' | 'assignees' | 'users' | 'teams')[];
  /** Page number */
  page?: number;
  /** Items per page */
  pageSize?: number;
}

/**
 * Workflows resource for Teamwork API.
 */
export class WorkflowsResource {
  constructor(private readonly client: TeamworkHttpClient) {}

  /**
   * List all workflows.
   */
  async list(options?: ListWorkflowsOptions): Promise<WorkflowListResponse> {
    const params: Record<string, string | number | boolean | string[] | undefined> = {};

    if (options?.projectIds?.length) {
      params['projectIds'] = options.projectIds.join(',');
    }
    if (options?.workflowIds?.length) {
      params['workflowIds'] = options.workflowIds.join(',');
    }
    if (options?.stageNames?.length) {
      params['stageNames'] = options.stageNames.join(',');
    }
    if (options?.searchTerm) {
      params['searchTerm'] = options.searchTerm;
    }
    if (options?.include?.length) {
      params['include'] = options.include;
    }
    if (options?.onlyDefaultWorkflow !== undefined) {
      params['onlyDefaultWorkflow'] = options.onlyDefaultWorkflow;
    }
    if (options?.includeArchived !== undefined) {
      params['includeArchived'] = options.includeArchived;
    }
    if (options?.includeTotalCount !== undefined) {
      params['includeTotalCount'] = options.includeTotalCount;
    }
    if (options?.page !== undefined) {
      params['page'] = options.page;
    }
    if (options?.pageSize !== undefined) {
      params['pageSize'] = options.pageSize;
    }

    const response = await this.client.get<WorkflowListResponse>('/projects/api/v3/workflows.json', params);
    return WorkflowListResponseSchema.parse(response);
  }

  /**
   * Get a single workflow by ID.
   */
  async get(workflowId: number, include?: ('stages' | 'projects')[]): Promise<Workflow> {
    const params: Record<string, string | string[] | undefined> = {};

    if (include?.length) {
      params['include'] = include;
    }

    const response = await this.client.get<WorkflowResponse>(
      `/projects/api/v3/workflows/${workflowId}.json`,
      params
    );
    const parsed = WorkflowResponseSchema.parse(response);
    return parsed.workflow;
  }

  /**
   * Get all stages for a workflow.
   */
  async getStages(workflowId: number): Promise<Stage[]> {
    const response = await this.client.get<StageListResponse>(
      `/projects/api/v3/workflows/${workflowId}/stages.json`
    );
    const parsed = StageListResponseSchema.parse(response);
    return parsed.stages;
  }

  /**
   * Get a specific stage.
   */
  async getStage(workflowId: number, stageId: number): Promise<Stage> {
    const response = await this.client.get<StageResponse>(
      `/projects/api/v3/workflows/${workflowId}/stages/${stageId}.json`
    );
    const parsed = StageResponseSchema.parse(response);
    return parsed.stage;
  }

  /**
   * Get tasks in a specific stage.
   */
  async getStageTasks(
    workflowId: number,
    stageId: number,
    options?: ListStageTasksOptions
  ): Promise<TaskListResponse> {
    const params: Record<string, string | number | string[] | undefined> = {};

    if (options?.include?.length) {
      params['include'] = options.include;
    }
    if (options?.page !== undefined) {
      params['page'] = options.page;
    }
    if (options?.pageSize !== undefined) {
      params['pageSize'] = options.pageSize;
    }

    const response = await this.client.get<TaskListResponse>(
      `/projects/api/v3/workflows/${workflowId}/stages/${stageId}/tasks.json`,
      params
    );
    return TaskListResponseSchema.parse(response);
  }

  /**
   * Get tasks in workflow backlog (not assigned to any stage).
   */
  async getBacklogTasks(workflowId: number, options?: ListStageTasksOptions): Promise<TaskListResponse> {
    const params: Record<string, string | number | string[] | undefined> = {};

    if (options?.include?.length) {
      params['include'] = options.include;
    }
    if (options?.page !== undefined) {
      params['page'] = options.page;
    }
    if (options?.pageSize !== undefined) {
      params['pageSize'] = options.pageSize;
    }

    const response = await this.client.get<TaskListResponse>(
      `/projects/api/v3/workflows/${workflowId}/backlog.json`,
      params
    );
    return TaskListResponseSchema.parse(response);
  }

  /**
   * Add tasks to a stage.
   */
  async addTasksToStage(
    workflowId: number,
    stageId: number,
    taskIds: number[],
    positionAfterCard?: number | null
  ): Promise<void> {
    const body: AddTaskToStageRequest = {
      cards: taskIds.map((taskId) => ({
        taskId,
        positionAfterCard: positionAfterCard ?? null,
      })),
    };

    await this.client.post(
      `/projects/api/v3/workflows/${workflowId}/stages/${stageId}/tasks.json`,
      body
    );
  }

  /**
   * Add a single task to a stage.
   */
  async addTaskToStage(
    workflowId: number,
    stageId: number,
    taskId: number,
    positionAfterCard?: number | null
  ): Promise<void> {
    await this.addTasksToStage(workflowId, stageId, [taskId], positionAfterCard);
  }

  /**
   * Move a task to a different stage (update position in workflow).
   */
  async moveTaskToStage(
    taskId: number,
    workflowId: number,
    stageId: number,
    positionAfterCard?: number | null
  ): Promise<void> {
    const body: UpdateTaskPositionRequest = {
      card: {
        columnId: stageId,
        positionAfterCard: positionAfterCard ?? null,
      },
    };

    await this.client.patch(`/projects/api/v3/tasks/${taskId}/workflows/${workflowId}.json`, body);
  }

  /**
   * Get workflows for a specific project.
   */
  async listByProject(projectId: number, include?: ('stages' | 'projects')[]): Promise<WorkflowListResponse> {
    return this.list({
      projectIds: [projectId],
      include,
    });
  }

  /**
   * Find a stage by name within a workflow.
   */
  async findStageByName(workflowId: number, stageName: string): Promise<Stage | null> {
    const stages = await this.getStages(workflowId);
    const lowerName = stageName.toLowerCase();
    return stages.find((stage) => stage.name.toLowerCase() === lowerName) ?? null;
  }

  /**
   * Get all tasks across all stages for a workflow.
   */
  async getAllWorkflowTasks(
    workflowId: number,
    options?: ListStageTasksOptions
  ): Promise<Map<number, TaskListResponse>> {
    const stages = await this.getStages(workflowId);
    const result = new Map<number, TaskListResponse>();

    for (const stage of stages) {
      const tasks = await this.getStageTasks(workflowId, stage.id, options);
      result.set(stage.id, tasks);
    }

    return result;
  }
}

/**
 * Comments resource module for Teamwork API.
 * Provides comment operations for tasks and other objects.
 */

import type { TeamworkHttpClient } from '../client.ts';
import {
  type Comment,
  type CommentListResponse,
  type CommentResponse,
  type CreateCommentRequest,
  CommentListResponseSchema,
  CommentResponseSchema,
} from '../types.ts';

export interface ListCommentsOptions {
  /** Include related data */
  include?: ('users')[];
  /** Page number */
  page?: number;
  /** Items per page */
  pageSize?: number;
}

export interface CreateCommentOptions {
  /** Comment body text */
  body: string;
  /** Content type (TEXT, HTML, or MARKDOWN) */
  contentType?: 'TEXT' | 'HTML' | 'MARKDOWN';
  /** Whether comment is private */
  isPrivate?: boolean;
}

/**
 * Format an ADW status update as a comment.
 */
export interface AdwStatusUpdate {
  /** ADW tracking ID */
  adwId: string;
  /** Status being updated to */
  status: string;
  /** Optional commit hash */
  commitHash?: string;
  /** Optional agent name */
  agentName?: string;
  /** Optional error message */
  errorMessage?: string;
  /** Optional additional message */
  message?: string;
}

/**
 * Comments resource for Teamwork API.
 */
export class CommentsResource {
  constructor(private readonly client: TeamworkHttpClient) {}

  /**
   * List comments for a task.
   */
  async listForTask(taskId: number, options?: ListCommentsOptions): Promise<CommentListResponse> {
    const params: Record<string, string | number | string[] | undefined> = {};

    if (options?.include?.length) {
      params['include'] = options.include;
    }
    if (options?.page !== undefined) {
      params['page'] = options.page;
    }
    if (options?.pageSize !== undefined) {
      params['pageSize'] = options.pageSize;
    }

    const response = await this.client.get<CommentListResponse>(
      `/projects/api/v3/tasks/${taskId}/comments.json`,
      params
    );
    return CommentListResponseSchema.parse(response);
  }

  /**
   * Create a comment on a task.
   */
  async createForTask(taskId: number, options: CreateCommentOptions): Promise<Comment> {
    const body: CreateCommentRequest = {
      comment: {
        body: options.body,
        contentType: options.contentType ?? 'MARKDOWN',
        isPrivate: options.isPrivate ?? false,
      },
    };

    const response = await this.client.post<CommentResponse>(
      `/projects/api/v3/tasks/${taskId}/comments.json`,
      body
    );
    const parsed = CommentResponseSchema.parse(response);
    return parsed.comment;
  }

  /**
   * Get a single comment by ID.
   */
  async get(commentId: number): Promise<Comment> {
    const response = await this.client.get<CommentResponse>(`/projects/api/v3/comments/${commentId}.json`);
    const parsed = CommentResponseSchema.parse(response);
    return parsed.comment;
  }

  /**
   * Update a comment.
   */
  async update(commentId: number, body: string, contentType?: 'TEXT' | 'HTML' | 'MARKDOWN'): Promise<Comment> {
    const response = await this.client.patch<CommentResponse>(`/projects/api/v3/comments/${commentId}.json`, {
      comment: {
        body,
        contentType: contentType ?? 'MARKDOWN',
      },
    });
    const parsed = CommentResponseSchema.parse(response);
    return parsed.comment;
  }

  /**
   * Delete a comment.
   */
  async delete(commentId: number): Promise<void> {
    await this.client.delete(`/projects/api/v3/comments/${commentId}.json`);
  }

  /**
   * Format an ADW status update as a markdown comment.
   */
  formatAdwStatusComment(update: AdwStatusUpdate): string {
    const emojiMap: Record<string, string> = {
      'In Progress': '🔄',
      'In progress': '🔄',
      'in progress': '🔄',
      Complete: '✅',
      complete: '✅',
      Completed: '✅',
      completed: '✅',
      Done: '✅',
      done: '✅',
      Failed: '❌',
      failed: '❌',
      Review: '👁️',
      review: '👁️',
      Blocked: '🚫',
      blocked: '🚫',
    };

    const emoji = emojiMap[update.status] ?? 'ℹ️';

    const lines: string[] = [
      `${emoji} **Status Update: ${update.status}**`,
      '',
      `- **ADW ID**: \`${update.adwId}\``,
      `- **Timestamp**: ${new Date().toISOString()}`,
    ];

    if (update.commitHash) {
      lines.push(`- **Commit**: \`${update.commitHash}\``);
    }

    if (update.agentName) {
      lines.push(`- **Agent**: ${update.agentName}`);
    }

    lines.push('');
    lines.push('---');

    if (update.errorMessage) {
      lines.push('');
      lines.push('**Error:**');
      lines.push('```');
      lines.push(update.errorMessage);
      lines.push('```');
    } else if (update.message) {
      lines.push('');
      lines.push(update.message);
    }

    return lines.join('\n');
  }

  /**
   * Post an ADW status update comment on a task.
   */
  async postAdwStatusUpdate(taskId: number, update: AdwStatusUpdate): Promise<Comment> {
    const body = this.formatAdwStatusComment(update);
    return this.createForTask(taskId, {
      body,
      contentType: 'MARKDOWN',
    });
  }

  /**
   * Post a simple text comment on a task.
   */
  async postText(taskId: number, text: string): Promise<Comment> {
    return this.createForTask(taskId, {
      body: text,
      contentType: 'TEXT',
    });
  }

  /**
   * Post a markdown comment on a task.
   */
  async postMarkdown(taskId: number, markdown: string): Promise<Comment> {
    return this.createForTask(taskId, {
      body: markdown,
      contentType: 'MARKDOWN',
    });
  }
}

